mapper.select:
select * from category
        aid!=0
            and aid=#{aid} 
            and category_id LIKE concat('%',#{category_id},'%') 
            and name LIKE concat('%',#{name},'%') 
            and status=#{status} 
            and create_time=#{create_time} 
            and create_time >=DATE_FORMAT(#{screate_time}, '%Y-%m-%d-%H-%M')
            and create_time <=DATE_FORMAT(#{ecreate_time}, '%Y-%m-%d-%H-%M')
            and update_time=#{update_time} 
            and update_time >=DATE_FORMAT(#{supdate_time}, '%Y-%m-%d-%H-%M')
            and update_time <=DATE_FORMAT(#{eupdate_time}, '%Y-%m-%d-%H-%M')
            and can_del=#{can_del} 
            and article_count=#{article_count} 

        order by ${orderByCase}
        limit #{startPos},#{pageSize}

select Count(*) from category
        aid!=0
            and aid=#{aid} 
            and category_id LIKE concat('%',#{category_id},'%') 
            and name LIKE concat('%',#{name},'%') 
            and status=#{status} 
            and create_time=#{create_time} 
            and create_time >=DATE_FORMAT(#{screate_time}, '%Y-%m-%d-%H-%M')
            and create_time <=DATE_FORMAT(#{ecreate_time}, '%Y-%m-%d-%H-%M')
            and update_time=#{update_time} 
            and update_time >=DATE_FORMAT(#{supdate_time}, '%Y-%m-%d-%H-%M')
            and update_time <=DATE_FORMAT(#{eupdate_time}, '%Y-%m-%d-%H-%M')
            and can_del=#{can_del} 
            and article_count=#{article_count}
